export const RIGHT_HAND_SIDE_SERIES = [
  {
    name: "Personal Promotion",
    data: [33, 14, 18],
    color: "#DB97D2",
  },
  {
    name: "Peer to Peer",
    data: [23, 29, 18],
    color: "#F3DBF1",
  },
];

export const LEFT_HAND_SIDE_SERIES = [
  {
    name: "Digital",
    data: [40, 57, 64],
    color: "#AD509E",
  },
];

export const LEFT_SIDE_CHART_OPTIONS = {
  chart: {
    id: "negative-bar-chart",
    stacked: true,
    toolbar: {
      show: false,
    },
    animations: {
      enabled: false,
    },
  },
  plotOptions: {
    bar: {
      horizontal: true,
      barHeight: "40%",
      dataLabels: {
        enabled: true,
        position: "center",
        style: {
          fontSize: "12px",
          colors: ["#000"],
        },
        formatter: function (val, opt) {
          return val + "%";
        },
      },
    },
  },
  xaxis: {
    categories: ["", "", ""],
  },
  yaxis: {
    reversed: true,
  },
  legend: {
    show: true,
  },
};
