import React from 'react';
import ReactApexChart from 'react-apexcharts';

var data = {
  series: [
    {
      name: "Series 1",
      data: [{
        x: 'W1',
        y: 22
      }, {
        x: 'W2',
        y: 29
      }, {
        x: 'W3',
        y: 13
      }, {
        x: 'W4',
        y: 32
      }]
    },
    {
      name: "Series 2",
      data: [{
        x: 'W1',
        y: 43
      }, {
        x: 'W2',
        y: 43
      }, {
        x: 'W3',
        y: 43
      }, {
        x: 'W4',
        y: 43
      }]
    }
  ]
}

class ApexChart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      series: data,
      options: {
        chart: {
          height: 450,
          type: "heatmap",
        },
        dataLabels: {
          enabled: false,
        },
        colors: ["#000000"],
        xaxis: {
          type: "category",
          categories: [
            "10:00",
            "10:30",
            "11:00",
            "11:30",
            "12:00",
            "12:30",
            "01:00",
            "01:30",
          ],
        },
        title: {
          text: "HeatMap Chart (Different color shades for each series)",
        },
        grid: {
          padding: {
            right: 20,
          },
        },
      },
    };
  }

  render() {
    return (
      <div id="chart">
        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="heatmap"
          height={450}
        />
      </div>
    );
  }
}
