import React from "react";
import Chart from "react-apexcharts";
// data: [16.7, 29.4, 17.7, 9.1, 24.8, 16.7, 18.7, 23.3],
const BarChart = () => {
  const colors = [
    "#FD853A",
    "#7A5AF8",
    "#12B76A",
    "#0BA5EC",
    "#2E90FA",
    "#6172F3",
    "#F63D68",
    "#EE46BC",
  ];
  const chartData = {
    options: {
      chart: {
        toolbar: {
          show: false,
        },
      },
      title: {
        text: "Digital Channel Affinity",
        align: "left",
      },
      colors: colors[3],
      plotOptions: {
        bar: {
          horizontal: false,
        //   distributed: true,
        },
      },
      legend: {
        show: true,
        position: "right",
      },
      grid: {
        show: false,
      },
      xaxis: {
        categories: [
          "E-mail",
          "Banners",
          "Programmatic",
          "Custom media",
          "Endemic social",
          "SMS",
          "Direct mail",
          "EHR",
        ],
      },
    },
  };
  const series = [
    {
      data: [21, 22, 10, 28, 16, 21, 13, 30],
    },

    //   {
    //     name: "Email",
    //     data: [16.7],
    //   },
    //   {
    //     name: "Banners",
    //     data: [29.4],
    //   },
    //   {
    //     name: "Programmatic",
    //     data: [17.7],
    //   },
    //   {
    //     name: "Custom media",
    //     data: [9.1],
    //   },
    //   {
    //     name: "Endemic social",
    //     data: [24.8],
    //   },
    //   {
    //     name: "SMS",
    //     data: [16.7],
    //   },
    //   {
    //     name: "Direct mail",
    //     data: [18.7],
    //   },
    //   {
    //     name: "EHR",
    //     data: [23.3],
    //   },
  ];

  return (
    <Chart
      options={chartData.options}
      series={series}
      type="bar"
      height={300}
    />
  );
};

export default BarChart;
