import { Box, Grid } from "@mui/material";
import React from "react";
import Chart from "react-apexcharts";

const NegativeBarChart = () => {
  const positiveChartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val) {
            return `${val}%`;
          },
        },
      },
    },
    xaxis: {
      categories: ["1st Preference", "2nd Preference", "3rd Preference"],
    },
    legend:{
      show:false
    }
  };
  const negativeChartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val) {
            return `${val}%`;
          },
        },
      },
    },
    xaxis: {
      categories: ["", "", ""],
    },
    legend:{
      show:false
    }
  };

  // const PositiveSeries = [
  //   {
  //     name: "Females",
  //     data: [33, 14, 18],
  //   },
  //   {
  //     name: "Trans",
  //     data: [23, 29, 18],
  //   },
  // ];
  // const NegativeSeries = [
  //   {
  //     name: "Males",
  //     data: [-40, -57, -64],
  //   },
  // ];


  const positiveseries = [
    {
      name: 'Positive',
      data: [
        {
          y: 5,
          color: '#FF0000', // Assign custom color to the bar
        },
        {
          y: 8,
          color: '#00FF00', // Assign custom color to the bar
        },
        3,
        6,
        {
          y: 2,
          color: '#0000FF', // Assign custom color to the bar
        },
      ],
    },
  ]

  const negativeSeries =[{
    name: 'Negative',
    data: [
      -3,
      {
        y: -4,
        color: '#FF00FF', // Assign custom color to the bar
      },
      -1,
      {
        y: -2,
        color: '#FFFF00', // Assign custom color to the bar
      },
      -5,
    ],
  }]

  return (
    <Grid sx={{ display: "flex", width: "100%" }}>
      <Box sx={{width:"50%"}}>
        <Chart
          options={negativeChartOptions}
          series={NegativeSeries}
          type="bar"
          height={600}
        />
      </Box>
      <Box sx={{width:"50%"}}>
        <Chart
          options={positiveChartOptions}
          series={PositiveSeries}
          type="bar"
          height={600}
        />
      </Box>
    </Grid>
  );
};

export default NegativeBarChart;
