import React from "react";
import { Box, Grid, Typography } from "@mui/material";
import Chart from "react-apexcharts";

const BarChart = () => {
  const chartData = {
    options: {
      chart: {
        toolbar: {
          show: false,
        },
      },
      plotOptions: {
        bar: {
          horizontal: false,
          distributed: true,
        },
      },
      colors: [
        "#FD853A",
        "#7A5AF8",
        "#12B76A",
        "#0BA5EC",
        "#2E90FA",
        "#6172F3",
        "#F63D68",
        "#EE46BC",
      ],
      legend: {
        show: false,
        // position: "right",
      },
      grid: {
        show: false,
      },
      xaxis: {
        categories: [
          "E-mail",
          "Banners",
          "Programmatic",
          "Custom media",
          "Endemic social",
          "SMS",
          "Direct mail",
          "EHR",
        ],
      },
    },
  };
  const series = [
    {
      data: [
        { x: "E-mail", y: 16.7, color: "#FD853A" },
        { x: "Banners", y: 29.4, color: "#7A5AF8" },
        { x: "Programmatic", y: 17.7, color: "#12B76A" },
        { x: "Custom media", y: 9.1, color: "#0BA5EC" },
        { x: "Endemic social", y: 24.8, color: "#2E90FA" },
        { x: "SMS", y: 16.7, color: "#6172F3" },
        { x: "Direct mail", y: 18.7, color: "#F63D68" },
        { x: "EHR", y: 23.3, color: "#EE46BC" },
      ],
    },
  ];

  return (
    <Grid container sx={{ border: "1px solid #E4E7EC", borderRadius: "10px" }}>
      <Grid
        item
        xs={12}
        sx={{ borderBottom: "1px solid  #E4E7EC", padding: "15px" }}
      >
        <Typography variant="h3"> Digital vs non-digital Affinity</Typography>
        <Typography variant="body1" sx={{ color: "#667085" }}>
          %, In percents
        </Typography>
      </Grid>
      <Grid item xs={12} sx={{ display: "flex", justifyContent: "center" }}>
        {["ALL", "1st Preference", "2nd Preference", "3rd Preference"].map(
          (legend, index) => (
            <Box
              key={index}
              sx={{
                display: "flex",
                alignItems: "center",
                marginRight: "24px",
              }}
            >
              <Box
                sx={{
                  width: "10px",
                  height: "10px",
                  background: legend.color,
                  marginRight: "8px",
                  borderRadius: "50%",
                }}
              ></Box>
              <Typography>{legend.name}</Typography>
            </Box>
          )
        )}
      </Grid>
      <Grid item sx={{ display: "flex", width: "100%" }}>
        <Chart
          options={chartData.options}
          series={series}
          type="bar"
          height={300}
        />
      </Grid>
    </Grid>
  );
};

export default BarChart;
