import React from "react";
import Chart from "react-apexcharts";
// data:
const BarChart = () => {
  const chartData = {
    options: {
      chart: {
        toolbar: {
          show: false,
        },
      },
      title: {
        text: "Digital Channel Affinity",
        align: "left",
      },

      plotOptions: {
        bar: {
          horizontal: false,
        },
      },
      legend: {
        show: true,
        position: "right",
      },
      grid: {
        show: false,
      },
      xaxis: {
        categories: [
          "E-mail",
          "Banners",
          "Programmatic",
          "Custom media",
          "Endemic social",
          "SMS",
          "Direct mail",
          "EHR",
        ],
      },
    },
  };
  const series = [
    {
      name: "Data",
      data: [
        { x: 16.7, color: "#FD853A" },
        { x: 29.4, color: "#7A5AF8" },
        { x: 17.7, color: "#12B76A" },
        { x: 9.1, color: "#0BA5EC" },
        { x: 24.8, color: "#2E90FA" },
        { x: 16.7, color: "#6172F3" },
        { x: 18.7, color: "#F63D68" },
        { x: 23.3, color: "#EE46BC" },
      ],
    },

    //   {
    //     name: "Email",
    //     data: [16.7],
    //   },
    //   {
    //     name: "Banners",
    //     data: [29.4],
    //   },
