    //     name: "Programmatic",
    //     data: [17.7],
    //   },
    //   {
    //     name: "Custom media",
    //     data: [9.1],
    //   },
    //   {
    //     name: "Endemic social",
    //     data: [24.8],
    //   },
    //   {
    //     name: "SMS",
    //     data: [16.7],
    //   },
    //   {
    //     name: "Direct mail",
    //     data: [18.7],
    //   },
    //   {
    //     name: "EHR",
    //     data: [23.3],
    //   },
  ];

  return (
    <Chart
      options={chartData.options}
      series={series}
      type="bar"
      height={300}
    />
  );
};

export default BarChart;
