import React from "react";
import Chart from "react-apexcharts";

const BarChart = () => {
  const chartData = {
    options: {
      chart: {

        toolbar: {
          show: false,
        },
      },
      title: {
        text: "Digital Channel Affinity",
        align: "left",
      },
      plotOptions: {
        bar: {
          horizontal: false,

        },
      },
      legend: {
        show: true,
        position: "right",
      },  
      grid:{
        show:false,
      },
      xaxis: {
        categories: [
          "E-mail",
          "Banners",
          "Programmatic",
          "Custom media",
          "Endemic social",
          "SMS",
          "Direct mail",
          "EHR"
        ],
      },
    },
    series: [
      {
        name: "E-mail",
        data: [16.7],
      },
      {
        name: "Banners",
        data: [29.4],
      },
      {
        name: "Programmatic",
        data: [17.7],
      },
      {
        name: "Custom media",
        data: [9.1],
      },
      {
        name: "Endemic social",
        data: [24.8],
      },
      {
        name: "SMS",
        data: [16.7],
      },
      {
        name: "Direct mail",
        data: [18.7],
      },
      {
        name: "EHR",
        data: [23.3],
      },
    ],
  };

  return (
    <Chart
      options={chartData.options}
      series={chartData.series}
      type="bar"
      height={300}
    />
  );
};

export default BarChart;
