import React from 'react';
import Chart from 'react-apexcharts';


const BarChart = () => {
    const chartData = {
      options: {
        chart: {
          toolbar: {
            show: false,
          },
        },
        title: {
            text: "Digital Channel Affinity",
            align: "left",
            margin: 10,
            offsetX: 0,
            offsetY: 0,
            floating: false,
            style: {
              fontSize: "14px",
              fontWeight: "bold",
              fontFamily: undefined,
              color: "#263238",
            },
          },
        plotOptions: {
          bar: {
            horizontal: false,
          },
        },
        xaxis: {
          categories: ['Category 1', 'Category 2', 'Category 3', 'Category 4', 'Category 5'],
        },
      },
      series: [
        {
          name: 'Data Series 1',
          data: [44, 55, 41, 67, 22],
        },
        {
          name: 'Data Series 2',
          data: [13, 23, 20, 8, 36],
        },
        {
          name: 'Data Series 3',
          data: [11, 17, 15, 15, 21],
        },
        {
          name: 'Data Series 4',
          data: [21, 7, 25, 13, 22],
        },
      ],
    };

    return (
      <Chart
        options={chartData.options}
        series={chartData.series}
        type="bar"
        height={300}
      />
    );
  };

  export default BarChart;
