import { Box, Grid } from "@mui/material";
import React from "react";
import Chart from "react-apexcharts";

const NegativeBarChart = () => {
  const chartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val) {
            return `${val}%`;
          },
        },
      },
    },
    xaxis: {
      categories: ["1st Preference", "2nd Preference", "3rd Preference"],
    },
    legend:{
      show:false
    }
  };

  const PositiveSeries = [
    {
      name: "Females",
      data: [33, 14, 18],
    },
    {
      name: "Trans",
      data: [23, 29, 18],
    },
  ];
  const NegativeSeries = [
    {
      name: "Males",
      data: [-40, -57, -64],
    },
  ];

  return (
    <Grid sx={{ display: "flex", width: "100%" }}>
      <Box sx={{width:"50%"}}>
        <Chart
          options={chartOptions}
          series={NegativeSeries}
          type="bar"
          height={600}
        />
      </Box>
      <Box sx={{width:"50%"}}>
        <Chart
          options={chartOptions}
          series={PositiveSeries}
          type="bar"
          height={600}
        />
      </Box>
    </Grid>
  );
};

export default NegativeBarChart;
