import { Box } from "@mui/material";
import React from "react";
import Chart from "react-apexcharts";

const NegativeBarChart = () => {
  const chartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val) {
            return `${val}%`;
          },
        },
      },
    },
    xaxis: {
      categories: [
        "Category 1",
        "Category 2",
        "Category 3",
        "Category 4",
        "Category 5",
      ],
      labels: {
        position: "center", // Set the position to 'center'
        formatter: function (val) {
          return Math.abs(val);
        },
      },
    },
  };

  const chartSeries = [
    {
      name: "Males",
      data: [-40, -57, -64],
    },
    {
      name: "Females",
      data: [33, 14, 18],
    },
    {
      name: "Trans",
      data: [23, 29, 18],
    },
  ];

  return (
    <>
      <div>
        <Chart
          options={chartOptions}
          series={chartSeries}
          type="bar"
          height={600}
        />
      </div>
    </>
  );
};

export default NegativeBarChart;
