import React from "react";
import { Box, Grid, Typography } from "@mui/material";
import HeatMapChart from "./charts/heatMapChart";
import HorizontalBarChart from "./charts/horizontalBarChart";
import BarChart from "./charts/barChart";
import { FILTER } from "./constants";
import { BasicSelect } from "../shared";

function Dashboard() {
  return (
    <Grid
      sx={{ paddingLeft: "32px", paddingRight: "32px", paddingTop: "32px" }}
    >
      {FILTER.map((item) => {
        return (
          <Box container>
            <BasicSelect filter={item} />
          </Box>
        );
      })}
      <Typography
        sx={{
          color: "#101828",
          fontFamily: "inter",
          fontWeight: "500",
          fontSize: "24px",
          lineHeight: "32px",
          paddingBottom: "24px",
        }}
      >
        Channel Affinity
      </Typography>
      <Grid
        container
        sx={{
          justifyContent: "space-between",
          background: "#F9FAFB",
          borderRadius: "10px",
          paddingLeft: "24px",
          paddingRight: "24px",
          paddingTop: "28px",
          marginBottom: "24px",
        }}
      >
        <HeatMapChart
          chartName="Digital Affinity"
          colors={["#FEDF89", "#F79009", "#B54708", "#7A2E0E", "#F79009"]}
        />
        <HeatMapChart
          chartName="Personal Promotion Affinity"
          colors={["#A6F4C5", "#12B76A", "#027A48", "#054F31", "#12B76A"]}
        />
        <HeatMapChart
          chartName="Peer to Peer Affinity"
          colors={["#B9E6FE", "#0BA5EC", "#026AA2", "#0B4A6F", "#0BA5EC"]}
        />
      </Grid>
      <Box sx={{ marginBottom: "24px" }}>
        <HorizontalBarChart />
      </Box>

      <Grid sx={{ border: "1px solid #E4E7EC", borderRadius: "10px" }}>
        <Grid
          item
          xs={12}
          sx={{ borderBottom: "1px solid  #E4E7EC", padding: "24px" }}
        >
          <Typography
            sx={{
              color: "#101828",
              fontFamily: "inter",
              fontWeight: "600",
              fontSize: "18px",
              lineHeight: "28px",
            }}
          >
            Digital Channel Affinity
          </Typography>
          <Grid container sx={{}}>
            <Grid item>
              {[
                "All",
                "1st Preference",
                "2nd Preference",
                "3rd Preference",
              ].map((preference) => {
                return (
                  <Box sx={{ padding: "4px", background: "#F9FAFB" }}>
                    <Typography
                      sx={{
                        color: "#101828",
                        fontFamily: "inter",
                        fontWeight: "600",
                        fontSize: "14px",
                        lineHeight: "20px",
                        padding: "8px 12px",
                        borderRadius: "6px",
                        boxShadow: "0px 1px ##101828",
                      }}
                    >
                      {preference}
                    </Typography>
                  </Box>
                );
              })}
            </Grid>
          </Grid>
        </Grid>
        <BarChart />
      </Grid>
    </Grid>
  );
}

export default Dashboard;
