import React from 'react';
import Chart from 'react-apexcharts';

const HeatMapChart = () => {
  const options = {
    chart: {
      type: 'heatmap',
    },
    plotOptions: {
      heatmap: {
        colorScale: {
          ranges: [
            {
              from: 0,
              to: 36,
              color: '#FEDF89', // Range 0-36 color
            },
            {
              from: 36,
              to: 70,
              color: '#F79009', // Range 36-70 color
            },
            {
              from: 70,
              to: 96,
              color: '#B54708', // Range 70-96 color
            },
            {
              from: 96,
              to: 100,
              color: '#7A2E0E', // Range 96-100 color
            },
          ],
        },
      },
    },
    xaxis: {
      categories: ['Category 1', 'Category 2', 'Category 3', 'Category 1', 'Category 2', 'Category 3', 'Category 1', 'Category 2', 'Category 3', '10'],
    },
  };

  const generateData = () => {
    const data = [];
    for (let i = 0; i < 100; i++) {
      const randomValue = Math.floor(Math.random() * 100) + 1;
      data.push({ x: `Category ${i + 1}`, y: randomValue });
    }
    return data;
  };

  const series = [
    {
      name: 'Series 1',
      data: generateData(),
    },
  ];

  return (
    <div>
      <Chart options={options} series={series} type="heatmap" height="350" />
    </div>
  );
};

export default HeatMapChart;
