import React from 'react';
import Chart from 'react-apexcharts';

const HeatMap = () => {
  const options = {
    chart: {
      type: 'heatmap',
    },
    plotOptions: {
      heatmap: {
        colorScale: {
          ranges: [
            {
              from: 0,
              to: 50,
              color: '#FF0000', // Red color for the range 0-50
            },
            {
              from: 51,
              to: 100,
              color: '#FFFF00', // Yellow color for the range 51-100
            },
            {
              from: 101,
              to: 150,
              color: '#00FF00', // Green color for the range 101-150
            },
          ],
        },
      },
    },
    xaxis: {
      categories: ['Category 1', 'Category 2', 'Category 3'],
    },
  };

  const series = [
    {
      name: 'Series 1',
      data: [
        { x: 'Category 1', y: 80 },
        { x: 'Category 2', y: 30 },
        { x: 'Category 3', y: 120 },
      ],
    },
  ];

  return (
    <div>
      <Chart options={options} series={series} type="heatmap" height="350" />
    </div>
  );
};

export default HeatMap;
