import { Box, Grid } from "@mui/material";
import React from "react";
import Chart from "react-apexcharts";
import { LEFT_HAND_SIDE_SERIES, RIGHT_HAND_SIDE_SERIES } from "./constants";

const BarChart = () => {
  const rightSideChartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val, opt) {
            return val + "%";
          },
        },
      },
    },
    xaxis: {
      categories: ["1st Preference", "2nd Preference", "3rd Preference"],
    },

    legend: {
      show: true,
      horizontalAlign: "center",
      position: "right",
    },
  };
  const leftSideChartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val, opt) {
            return val + "%";
          },
        },
      },
    },
    xaxis: {
      categories: ["", "", ""],
    },
    yaxis: {
      reversed: true,
    },
    legend: {
      show: true,

      position: "right",
    },
  };

  return (
    <Grid sx={{ display: "flex", width: "100%" }}>
      <Box sx={{ width: "50%" }}>
        <Chart
          options={leftSideChartOptions}
          series={LEFT_HAND_SIDE_SERIES}
          type="bar"
          height={600}
        />
      </Box>
      <Box sx={{ width: "50%" }}>
        <Chart
          options={rightSideChartOptions}
          series={RIGHT_HAND_SIDE_SERIES}
          type="bar"
          height={600}
        />
      </Box>
    </Grid>
  );
};

export default BarChart;
