import { Box, Grid } from "@mui/material";
import React from "react";
import Chart from "react-apexcharts";

const BarChart = () => {
  const rightSideChartOptions = {
    chart: {
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val, opt) {
            return val+"%";
          },
        },
      },
    },
    xaxis: {
      categories: ["1st Preference", "2nd Preference", "3rd Preference"],
    },

    legend: {
      show: false,
    },
  };
  const leftSideChartOptions = {
    chart: {
      id: "negative-bar-chart",
      stacked: true,
      toolbar: {
        show: false,
      },
      animations: {
        enabled: false,
      },
    },
    plotOptions: {
      bar: {
        horizontal: true,
        barHeight: "40%",
        dataLabels: {
          enabled: true,
          position: "center",
          style: {
            fontSize: "12px",
            colors: ["#000"],
          },
          formatter: function (val, opt) {
            return val+"%";
          },
        },
      },
    },
    xaxis: {
      categories: ["", "", ""],
    },
    yaxis: {    
      reversed: true,
    },
    legend: {
      show: false,
    },
  };

  const leftSideChartSeries = [
    {
      name: "Personal Promotion",
      data: [33, 14, 18],
      color: "#DB97D2",
    },
    {
      name: "Peer to Peer",
      data: [23, 29, 18],
      color: "#F3DBF1",
    },
  ];
  const rightSideChartSeries = [
    {
      name: "Digital",
      data: [40, 57, 64],
      color: "#AD509E",
    },
  ];

  return (
    <Grid sx={{ display: "flex", width: "auto" }}>
      <Box sx={{ width: "50%" }}>
        <Chart
          options={leftSideChartOptions}
          series={leftSideChartSeries}
          type="bar"
          height={600}
        />
      </Box>
      <Box sx={{ width: "50%" }}>
        <Chart
          options={rightSideChartOptions}
          series={rightSideChartSeries}
          type="bar"
          height={600}
        />
      </Box>
    </Grid>
  );
};

export default NegativeBarChart;
