import React from "react";
import { Box, Grid, Typography } from "@mui/material";
import HeatMapChart from "./charts/heatMapChart";
import HorizontalBarChart from "./charts/horizontalBarChart";
import BarChart from "./charts/barChart";

function Dashboard() {
  return (
    <Grid
      sx={{ paddingLeft: "32px", paddingRight: "32px", paddingTop: "32px" }}
    >
      <Typography
        sx={{
          color: "#101828",
          fontFamily: "inter",
          fontWeight: "500",
          fontSize: "24px",
          lineHeight: "32px",
          paddingBottom: "24px",
        }}
      >
        Channel Affinity
      </Typography>
      <Grid
        container
        sx={{
          justifyContent: "space-between",
          background: "#F9FAFB",
          borderRadius: "10px",
          paddingLeft: "24px",
          paddingRight: "24px",
          paddingTop: "28px",
          marginBottom: "24px",
        }}
      >
        <HeatMapChart
          chartName="Digital Affinity"
          colors={["#FEDF89", "#F79009", "#B54708", "#7A2E0E", "#F79009"]}
        />
        <HeatMapChart
          chartName="Personal Promotion Affinity"
          colors={["#A6F4C5", "#12B76A", "#027A48", "#054F31", "#12B76A"]}
        />
        <HeatMapChart
          chartName="Peer to Peer Affinity"
          colors={["#B9E6FE", "#0BA5EC", "#026AA2", "#0B4A6F", "#0BA5EC"]}
        />
      </Grid>
      <Box sx={{ marginBottom: "24px" }}>
        <HorizontalBarChart />
      </Box>

      <Grid
        container
        sx={{ border: "1px solid #E4E7EC", borderRadius: "10px" }}
      >
        <Grid
          item
          xs={12}
          sx={{ borderBottom: "1px solid  #E4E7EC", padding: "24px" }}
        >
          <Typography
            sx={{
              color: "#101828",
              fontFamily: "inter",
              fontWeight: "600",
              fontSize: "18px",
              lineHeight: "28px",
            }}
          >
            Digital Channel Affinity
          </Typography>
        </Grid>
        <BarChart />
      </Grid>
    </Grid>
  );
}

export default Dashboard;
